package FirstProgramm;

public class Multiplication_Table {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int z;	
	for(int i=1; i<=5;i++)
	{
		System.out.println(i+" table");
		System.out.println("**********");
		for(int j=1;j<=12;j++)
		{
			z= i*j;
			System.out.println(i+ "*"+j + " ="+ z);
		}
		System.out.println();
		}
			
	}

	}


