package FirstProgramm;

public class NumberSwapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Number Swapping
		
		int i,j,k;
	i=10;
	j=20;
	
	System.out.println("Before swapping the value of i is"+ " " +i);	
	System.out.println();
	System.out.println("Before swapping the value of j is "+ " " +j);
	System.out.println();
	k=j;
	j=i;
	i=k;
	System.out.println();
	System.out.println("After swapping the value of i is"+" "+i);
	System.out.println();
	System.out.println("After swapping the value of j is"+ " "+j);
	
	
		
	}

}
